configuration:
{{- if and .Values.distStorage.type (ne .Values.distStorage.type "local") }}
{{- if eq .Values.distStorage.type "aws" }}
fs.dremioS3.impl The FileSystem implementation. Must be set to com.dremio.plugins.s3.store.S3FileSystem com.dremio.plugins.s3.store.S3FileSystem
fs.s3a.aws.credentials.provider The credential provider type. org.apache.hadoop.fs.s3a.SimpleAWSCredentialsProvider
fs.s3a.access.key AWS access key ID. {{ required "AWS access key required" .Values.distStorage.aws.accessKey}}
fs.s3a.secret.key AWS secret key. {{ required "AWS secret required" .Values.distStorage.aws.secret}}
{{- end }}
{{- if eq .Values.distStorage.type "azure" }}
fs.adl.impl Must be set to org.apache.hadoop.fs.adl.AdlFileSystem org.apache.hadoop.fs.adl.AdlFileSystem
dfs.adls.oauth2.client.id Application ID of the registered application under Azure Active Directory {{required "Azure application ID required" .Values.distStorage.azure.applicationId}}
dfs.adls.oauth2.credential Generated password value for the registered application {{required "Azure secret value required" .Values.distStorage.azure.secret}}
dfs.adls.oauth2.refresh.url Azure Active Directory OAuth 2.0 Token Endpoint for registered applications. {{required "Azure OAuth2 token endpoint required" .Values.distStorage.azure.oauth2EndPoint}}
dfs.adls.oauth2.access.token.provider.type Must be set to ClientCredential ClientCredential
fs.adl.impl.disable.cache Only include this property AFTER validating the ADLS connection. false
{{- end }}
{{- if eq .Values.distStorage.type "azureStorage" }}
fs.dremioAzureStorage.impl FileSystem implementation. Must always be com.dremio.plugins.azure.AzureStorageFileSystem com.dremio.plugins.azure.AzureStorageFileSystem
dremio.azure.account The name of the storage account. {{required "Azure storage account name required" .Values.distStorage.azureStorage.accountName}}
dremio.azure.key The shared access key for the storage account. {{required "Shared access key for the storage account required" .Values.distStorage.azureStorage.accessKey}}
dremio.azure.mode The storage account type. Value: STORAGE_V2 STORAGE_V2
dremio.azure.secure Boolean option to enable SSL connections. Value: True/False True
{{- end }} {{- end }}
